mapper.update:
UPDATE REST SET
rest_check = 'T', rest_result =#{restResult}, rest_confirm = to_char(sysdate, 'yyyy-MM-dd') 
WHERE rest_no =#{restNo}

mapper.select:
SELECT member_id 
FROM REST
WHERE rest_no =#{restNo}

SELECT recruit_num
FROM REST
WHERE rest_no =#{restNo}

SELECT member_id 
FROM recruit
WHERE recruit_num =#{recruit_num}

mapper.update:
UPDATE member set 
rest_count = to_number(rest_count) + 1 
WHERE member_id=#{memberId}
